const TOKEN_TYPE = Object.freeze({
    UNARYOP:   Symbol("unaryop"),     // 一元运算符
    BINARYOP:  Symbol("binaryop"),    // 二元运算符
    OPRAND: Symbol("operand"),        // 操作数
    LEFTPAREN: Symbol("leftparen"),   // 左括号
    RIGHTPAREN: Symbol("rightparen"), // 右括号
    ENDEXPR: Symbol("endexpr"),       // 终止符号
})


class Token {
    constructor(name, type, priority = 0, val = 0) {
        this.name = name
        this.type = type
        this.priority = priority
        this.val = val
    }

    isNumber() {
        return this.type === TOKEN_TYPE.OPRAND
    }
}



module.exports.Token = Token


module.exports.TOKEN_TYPE = TOKEN_TYPE